package com.solab.iso8583;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.Arguments;
import org.junit.jupiter.params.provider.MethodSource;

import java.util.Arrays;
import java.util.Collection;
import java.util.stream.Stream;

public class TestIsoTypeMaxLength {

    private static final String LONG_VALUE = "";

//    @Parameterized.Parameters(name = "type={0}, expected={1}")
    public static Stream<Arguments> data() {
        return Stream.of(
            Arguments.of(IsoType.LLVAR, "LLVAR can only hold values up to 99 chars"),
            Arguments.of(IsoType.LLLVAR, "LLLVAR can only hold values up to 999 chars"),
            Arguments.of(IsoType.LLLLVAR, "LLLLVAR can only hold values up to 9999 chars"),
            Arguments.of(IsoType.LLBIN, "LLBIN can only hold values up to 99 chars"),
            Arguments.of(IsoType.LLLBIN, "LLLBIN can only hold values up to 999 chars"),
            Arguments.of(IsoType.LLLLBIN, "LLLLBIN can only hold values up to 9999 chars"),
            Arguments.of(IsoType.LLBCDBIN, "LLBCDBIN can only hold values up to 50 chars"),
            Arguments.of(IsoType.LLLBCDBIN, "LLLBCDBIN can only hold values up to 500 chars"),
            Arguments.of(IsoType.LLLLBCDBIN, "LLLLBCDBIN can only hold values up to 5000 chars")
        );
    }

    @ParameterizedTest
    @MethodSource("data")
    public void shouldThrowIllegalArgumentExceptionWhenUsingLengthInConstructor(IsoType type, String expected) {
        try {
            // When
            new IsoValue<>(type, "value", 1000000);
            Assertions.fail("IllegalArgumentException expected.");
        } catch (IllegalArgumentException e) {
            // Then
            Assertions.assertEquals(expected, e.getMessage());
        }
    }

    @ParameterizedTest
    @MethodSource("data")
    public void shouldThrowIllegalArgumentExceptionWhithoutUsingLengthInConstructor(IsoType type, String expected) {
        try {
            // When
            new IsoValue<>(type, LONG_VALUE);
            Assertions.fail("IllegalArgumentException expected.");
        } catch (IllegalArgumentException e) {
            // Then
            Assertions.assertEquals(expected, e.getMessage());
        }
    }

}
